w3 = Web3(Web3.HTTPProvider('https://mainnet.aurora.dev'))
if w3.isConnected():
    contract = w3.eth.contract(
        address='0x35CC71888DBb9FfB777337324a4A60fdBAA19DDE', 
        abi=abi
    )
    near_contract = w3.eth.contract(
        address=w3.toChecksumAddress('0xc42c30ac6cc15fac9bd938618bcaa1a1fae8501d'), 
        abi=erc20_abi
    )
    eth_contract = w3.eth.contract(
        address=w3.toChecksumAddress('0xc9bdeed33cd01541e1eed10f90519d2c06fe3feb'), 
        abi=erc20_abi
    )

app = Flask(__name__)

@app.route('/')
def about():
    return 'Coding Task Smart Contract Dev'

@app.route('/yearly', methods = ['GET'])
def yearly_apr():
    if w3.isConnected():
        poolLength = contract.functions.poolLength().call()
        for i in range(0, poolLength):
            pool = contract.functions.poolInfo(i).call()
            if (near_contract.functions.balanceOf(pool[0]).call() > 0 and
                eth_contract.functions.balanceOf(pool[0]).call() > 0):
                pool_contract = w3.eth.contract(pool[0], abi=erc20_abi)
                break
        if 'pool_contract' in locals():
            current_block = w3.eth.get_block('latest')
            multiplier = contract.functions.getMultiplier(
                current_block.number, 
                current_block.number + 1
            ).call()
            total_allocation_points = contract.functions.totalAllocPoint().call()
            brl_per_block = ((contract.functions.BRLPerBlock().call() / (10 ** 18)) * 
                             pool[1] / total_allocation_points)
            rewards_per_year = brl_per_block * multiplier * (60 * 60 * 24 * 365) / 1.1
            per_share = pool[3] / (10 ** 12)
            total_supply = pool_contract.functions.totalSupply().call() / (10 ** 18)
            apr = rewards_per_year / per_share / total_supply
            return f"NEAR-WETH yearly APR {apr}"
        else:
            return 'Pool not found'
    else:
        return 'Not connected Aurora chain'
